import React, {useContext} from "react";
import ContactContaxt from '../../context/contact/contactContext';


const ContactsItem = ({contact})  => {
    const contactContext = useContext(ContactContaxt);

    return (
        <div className="contact_card">
            <h1 className="name">
                {contact.name} &nbsp;&nbsp;
             <span className={`type ${contact.type === "personal" ? "personal" : "professional"}`}>{contact.type}</span>
             </h1>
            <h2 className="email"><i className="fas fa-envelope-open"></i>{contact.email}</h2>
            <h2 className="email"><i className="fas fa-mobile"></i>{contact.phone}</h2>
            <div>
                <button className="btn edit_btn" style={{marginRight: '10px'}}>Edit</button>
                <button className="btn delete_btn">Delete</button>
            </div>
        </div>
    )    
}

export default ContactsItem;
