import React, { useState, useContext } from 'react';
import ContactContaxt from '../../context/contact/contactContext';

const ContactForm = () => {
    const contactContext = useContext(ContactContaxt);

    const [contact, setContact] = useState({
        name: "",
        email: "",
        phone: "",
        type: "personal",
    })

    const { email, name, phone, type } = contact;

    const onChange = e => {
        console.log(e.target.name)
        setContact({ ...contact, [e.target.name]: e.target.value })
    }

    const onSubmit = (e) => {
        e.preventDefault();
        contactContext.addContact(contact);
        // console.log(contactContext.addContact)
        setContact({
            name: "",
            email: '',
            phone: "",
            type: "personal"

        })
    }


    return (
        <div style={{ width: '400px' }}>
            <h1>Contact Form</h1>
            <div className="form_group">
                <label>Name</label>
                <input type="text" name="name" value={name} className="input" onChange={onChange} />
            </div>
            <div className="form_group">
                <label>Email</label>
                <input type="email" name="email" value={email} className="input" onChange={onChange} />
            </div>
            <div className="form_group">
                <label>Phone</label>
                <input type="phone" name="phone" value={phone} className="input" onChange={onChange} />
            </div>
            <div className="form_group">
                <label>Contact Type</label>
                <input type="radio" name="type" value="personal" className="" onChange={onChange} checked={type === "personal"}/> Personal
                <input type="radio" name="type" value="professional" className="" onChange={onChange}  checked={type === "professional"}/>
            </div>
            <div className="form_group" onClick={onSubmit}>
                <button className="btn edit_btn">Add</button>
            </div>
        </div>
    )
}



export default ContactForm;
