import React from 'react';

const ProductCard = ({ product }) => {

    const { name, decription, price, images } = product;
    console.log("FROM CARD", product)


    return (
        <div id="product-front">
            <div className="shadow"></div>
            <div style={{ textAlign: 'center' }}>
                {images && images.map((image, index) => {
                    if (index === 0) {
                        return <img src={image} alt="" style={{ maxWidth: '200px' }} />
                    }
                })}
            </div>

            <div className="image_overlay"></div>
            <div className="stats">
                <div className="stats-container">
                    <div className="product_name">{name && name.substr(0, 25) + "..."}</div>
                    <div className="product_price">{price.price}</div>
                  
                    <p>{decription}</p>
                </div>
            </div>

            <div style={{display: "flex", alignItems: 'center', justifyContent: 'space-between'}}>
                <button>Add Cart</button>
                <button>Buy Now</button>

            </div>
        </div>

    )
}


export default ProductCard;
