import React from 'react';

const ProductCard = ({product}) => {

    const {name, decription, price, images} = product;
    console.log("FROM CARD",product)

    
    return (
        <div id="make-3D-space">
            <div id="product-card">
                <div id="product-front">
                    <div className="shadow"></div>
                   <div style={{}}>
                   {images && images.map((image, index) => {
                        if(index === 0){
                            return <img src={image} alt="" style={{maxWidth: '200px'}} />
                        }
                    })}
                   </div>
                   
                    <div className="image_overlay"></div>
                    <div className="stats">
                        <div className="stats-container">
                            <span className="product_price">{price.price}</span>
                            <span className="product_name">{name && name.substr(0, 25) + "..."}</span>
                            <p>{decription}</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}


export default ProductCard;
